    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String nome     = txtNome.getText();
        float  preco    = Float.valueOf(txtPreco.getText());
        float  desconto = Float.valueOf(txtDesconto.getText());
        
        float valorDesc = (preco * desconto) / 100;
        float valorFinal = preco - valorDesc;
        
        txtResposta.setText(
                "O produto " + nome + " atualmente custa R$" + preco + 
                ". Com desconto de " + desconto + "%, este produto custa R$" + 
                valorFinal);
    }//GEN-LAST:event_jButton1ActionPerformed

